import React, {useState, useEffect} from 'react';
import './Bakery.css';
import { Outlet, Link } from "react-router-dom";
import logo from './Images/what.jpg';


function Home(){

	// State to track scroll position
	const [isVisible, setIsVisible] = useState(false);

	// Function to handle scroll event
   const handleScroll = () => {
	   if (window.scrollY > 300) {
		 setIsVisible(true);  // Show button after scrolling 300px
	   } else {
		 setIsVisible(false); // Hide button when at the top
	   }
	 };

   // Scroll to top function
   const scrollToTop = () => {
	   window.scrollTo({
		 top: 0,
		 behavior: 'smooth',  // Smooth scrolling effect
	   });
	 };

	 // useEffect to listen to the scroll event
   useEffect(() => {
	   window.addEventListener('scroll', handleScroll);
	   return () => {
		 window.removeEventListener('scroll', handleScroll);  // Cleanup on component unmount
	   };
	 }, []);



    return(
        <>
        <header class="header sticky-top" id="Bakeryhome">
			<nav class="na navbar navbar-expand-md">
			<div class="container-fluid">
				<div class="logo">
					<img src={logo} alt='logo' width='80px' height='80px'/>
				</div>
				<div class="collapse navbar-collapse" id="mynav">
					<ul class="link ms-auto me-5">
						<li class="list"><a href="/" class="anchor1 active">HOME</a></li>
						<li class="list"><a href="/about" class="anchor2 active" >ABOUT US</a></li>
						<li class="list"><a href="#Product" class="anchor3 active">PRODUCTS</a></li>
						<li class="list"><a href="#Specialities" class="anchor3 active">SPECIALITIES</a></li>
						<li class="list"><a href="/order" class="anchor4 active">ORDER</a></li>
					</ul>
				</div>
			</div>
			</nav>
		</header>

		{/* Back-to-top button */}
		{isVisible && (
          <button onClick={scrollToTop} class="go-to-top">
        	<i class="fa fa-angle-up" aria-hidden="true"></i>
          </button>
        )}

        <Outlet />
        </>
    );
}

export default Home;
